function Photo(props) {
    return (
        <div className="card bg-dark text-dark mb-2">
            <img 
                className="card-img" 
                src={"http://localhost:3001/" + props.photo.path} 
                alt={props.photo.name} 
                style={{ width: "300px", height: "300px", objectFit: "cover" }} // Nastavljena velikost slike
            />
            <div className="card-img-overlay">
                <h5 className="card-title">{props.photo.name}</h5>
                <p className="card-text">{props.photo.description}</p>
                <p className="card-text text-muted">Objavljeno: {new Date(props.photo.createdAt).toLocaleString()}</p> {/* Datum objave */}
            </div>
        </div>
    );
}

export default Photo;
